import React, { useState } from "react";

const ForgotPassword = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);

  return (
    <>
      {/* Forgot Password Link */}
      <a onClick={() => setIsModalOpen(true)} className="custom-options">
        Forgot Password?
      </a>

      {/* Forgot Password Modal */}
      <div className={`forgot-password-modal ${isModalOpen ? "active" : ""}`}>
        <div className="forgot-password-box">
          <h2>Forgot Password</h2>
          <input
            type="email"
            className="forgot-password-input"
            placeholder="Enter your email"
          />
          <button className="forgot-password-button">Send Reset Link</button>
          <button onClick={() => setIsModalOpen(false)} className="forgot-password-close">
            Close
          </button>
        </div>
      </div>
    </>
  );
};

export default ForgotPassword;
