import React, { useState } from "react";
import "./;login.css"; // Ensure this file is correctly linked

const Login = () => {
  const [showForgotPassword, setShowForgotPassword] = useState(false);

  return (
    <div className="custom-login-page">
      {/* Left Section */}
      <div className="custom-left-section">
        <h1>Welcome to Psychotherapy Consultancy</h1>
        <p>Providing professional psychotherapy services to help you navigate lifeâ€™s challenges.</p>
      </div>

      {/* Right Section */}
      <div className="custom-right-section">
        <div className="custom-login-box">
          <h3>User Login</h3>
          <input type="email" placeholder="Email" className="custom-input" />
          <input type="password" placeholder="Password" className="custom-input" />
          <div className="custom-options">
            <label>
              <input type="checkbox" /> Remember Me
            </label>
            <button onClick={() => setShowForgotPassword(true)} className="forgot-password-btn">
              Forgot password?
            </button>
          </div>
          <button className="custom-login-button">Login</button>
        </div>
      </div>

      {/* Forgot Password Modal */}
      {showForgotPassword && (
        <div className="forgot-password-modal">
          <div className="modal-content">
            <h2>Reset Password</h2>
            <input type="email" placeholder="Enter your email" className="custom-input" />
            <button className="custom-login-button">Submit</button>
            <button onClick={() => setShowForgotPassword(false)} className="close-modal">Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Login;
