import React, { useEffect, useState } from "react";
import io from "socket.io-client";
import "./chat.css";

const socket = io("http://localhost:5000");

const Chat = () => {
  const [room, setRoom] = useState("text-therapy-room");
  const [message, setMessage] = useState("");
  const [chat, setChat] = useState([]);

  const username = (localStorage.getItem("username") || "Anonymous").toUpperCase();

  useEffect(() => {
    socket.emit("join_room", room);

    socket.on("receive_message", (data) => {
      setChat((prev) => [...prev, data]);
    });

    return () => socket.off("receive_message");
  }, [room]);

  const sendMessage = () => {
    if (message.trim() === "") return;

    const data = {
      room,
      author: username,
      content: message,
      time: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
    };

    socket.emit("send_message", data);
    setChat((prev) => [...prev, data]);
    setMessage("");
  };

  return (
    <div className="chat-container">
      <div className="chat-header">Chat Room</div>
      <div className="chat-subheader">Welcome, <strong>{username}</strong></div>

      <div className="chat-messages">
        {chat.map((msg, idx) => (
          <div
            key={idx}
            className={`message ${msg.author === username ? "you" : "other"}`}
          >
            <div><strong>{msg.author}</strong></div>
            <div>{msg.content}</div>
            <div style={{ fontSize: "0.75rem", color: "#555", marginTop: "4px" }}>
              {msg.time}
            </div>
          </div>
        ))}
      </div>

      <div className="chat-input">
        <input
          type="text"
          placeholder="Type a message..."
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && sendMessage()}
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
};

export default Chat;
