import React, { useEffect, useState } from "react";
import io from "socket.io-client";
import "./chat.css";

const socket = io("http://localhost:5000"); // Your backend URL

const Chat = () => {
  const [message, setMessage] = useState("");
  const [chatMessages, setChatMessages] = useState([]);

  const username = localStorage.getItem("username")?.toUpperCase() || "USER";
  const role = localStorage.getItem("role")?.toUpperCase() || "GUEST";
  const room = "therapy-room"; // You can make this dynamic later

  useEffect(() => {
    socket.emit("join_room", room);

    socket.on("receive_message", (data) => {
      setChatMessages((prev) => [...prev, data]);
    });

    return () => {
      socket.off("receive_message");
    };
  }, [room]);

  const sendMessage = () => {
    if (message.trim()) {
      const msgData = {
        username,
        role,
        room,
        message,
        time: new Date().toLocaleTimeString(),
      };

      socket.emit("send_message", msgData);
      setChatMessages((prev) => [...prev, msgData]);
      setMessage("");
    }
  };

  return (
    <div className="chat-container">
      <h2>Chat Room</h2>
      <div className="chat-subheader">
        {role === "THERAPIST" ? "üë©‚Äç‚öïÔ∏è Therapist" : "üôã‚Äç‚ôÇÔ∏è User"}: <strong>{username}</strong>
      </div>

      <div className="chat-box">
        {chatMessages.map((msg, index) => (
          <div key={index} className={`chat-bubble ${msg.username === username ? "you" : "other"}`}>
            <span className="chat-user">{msg.username} ({msg.role})</span>
            <p>{msg.message}</p>
            <span className="chat-time">{msg.time}</span>
          </div>
        ))}
      </div>

      <div className="chat-input-row">
        <input
          type="text"
          placeholder="Type a message..."
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && sendMessage()}
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
};

export default Chat;
