import React, { useState } from 'react';
import './login.css'; // Import CSS file
import { Helmet } from 'react-helmet'
function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleUsernameChange = (e) => {
    setUsername(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (username === '' || password === '') {
      setError('Please fill in both fields');
    } else {
      setError('');
      console.log('Logging in with', username, password);
    }
  };

  return (
    <div className="custom-login-page">
      <Helmet>
        <title>psychotherapy consultancy</title>
      </Helmet>
      <div className="custom-left-section">
        <h2>Welcome to Psychotherapy consultancy</h2>
        <p>
        At Psychotherapy consultancy, we provide compassionate and professional psychotherapy services to help you navigate lifeâ€™s challenges. Whether you're dealing with stress, anxiety, depression, relationship issues, or personal growth, our expert therapists are here to support you.
        </p>
      </div>
      <div className="custom-right-section">
        <div className="custom-login-box">
          <h3>User Login</h3>
          <form onSubmit={handleSubmit}>
            <div className="custom-input-group">
              <input
                type="text"
                value={username}
                onChange={handleUsernameChange}
                placeholder="Username"
                className="custom-input"
              />
            </div>
            <div className="custom-input-group">
              <input
                type="password"
                value={password}
                onChange={handlePasswordChange}
                placeholder="Password"
                className="custom-input"
              />
            </div>
            {error && <p className="custom-error-message">{error}</p>}
            <div className="custom-options">
              <label>
                <input type="checkbox" /> Remember
              </label>
              <a href="#">Forgot password?</a>
            </div>
            <button type="submit" className="custom-login-button">Login</button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Login;
