import React, { useEffect, useState } from 'react';
import './appointment.css';

const Appointments = () => {
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      setError("User not authenticated. Please log in.");
      setLoading(false);
      return;
    }

    fetch("http://localhost:5000/api/appointments/past", {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
      .then((res) => {
        if (!res.ok) {
          throw new Error("Failed to fetch appointments.");
        }
        return res.json();
      })
      .then((data) => {
        setAppointments(data);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Fetch error:", err);
        setError("Something went wrong while fetching appointments.");
        setLoading(false);
      });
  }, []);

  return (
    <div className="appointments-page">
      <h2>Your Past Appointments</h2>
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p style={{ color: 'red' }}>{error}</p>
      ) : appointments.length === 0 ? (
        <p>No past appointments found.</p>
      ) : (
        appointments.map((apt, index) => (
          <div key={index} className="appointment-card">
            <h3>{apt.mainService}</h3>
            <p>Sub-Service: {apt.subService}</p>
            <p>Name: {apt.name}</p>
            {apt.spouseName && <p>Spouse: {apt.spouseName}</p>}
            {apt.childName && <p>Child: {apt.childName}</p>}
            <p>Date: {apt.date}</p>
            <p>Time: {apt.time}</p>
            <p>Contact: {apt.mobile}</p>
            <p>Email: {apt.email}</p>
          </div>
        ))
      )}
    </div>
  );
};

export default Appointments;
