import React, { useEffect, useState } from 'react';
import './appointment.css';

const Appointments = () => {
  const [appointments, setAppointments] = useState([]);
  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchAppointments = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/appointments", {
          method: "GET",
          headers: {
            "Authorization": `Bearer ${token}`,
          },
        });

        if (!res.ok) {
          throw new Error(`Error: ${res.status}`);
        }

        const data = await res.json();
        setAppointments(data);
      } catch (err) {
        console.error("‚ùå Failed to fetch appointments:", err);
      }
    };

    if (token) {
      fetchAppointments();
    } else {
      console.warn("üö´ No token found. User may not be logged in.");
    }
  }, [token]);

  return (
    <div className="appointments-page">
      <h2>Your Appointments</h2>
      {appointments.length === 0 ? (
        <p>No appointments found.</p>
      ) : (
        appointments.map((apt, index) => (
          <div key={index} className="appointment-card">
            <h3>{apt.mainService}</h3>
            <p><strong>Sub-Service:</strong> {apt.subService}</p>
            <p><strong>Name:</strong> {apt.name}</p>
            {apt.spouseName && <p><strong>Spouse:</strong> {apt.spouseName}</p>}
            {apt.childName && <p><strong>Child:</strong> {apt.childName}</p>}
            <p><strong>Date:</strong> {apt.date}</p>
            <p><strong>Time:</strong> {apt.time}</p>
            <p><strong>Contact:</strong> {apt.mobile}</p>
            <p><strong>Email:</strong> {apt.email}</p>
          </div>
        ))
      )}
    </div>
  );
};

export default Appointments;
